import React, { Component } from 'react'
import stylesProduct from '../screens/CSS/productInfor.module.css';
import {useState} from 'react';

const ButtonChangeValue = () => {
    const [message, setMessage] = useState('');
  
    const handleChange = event => {
      setMessage(event.target.value);
    };
  
    const handleClick = event => {
      event.preventDefault();
  
      // üëáÔ∏è value of input field
      console.log('old value: ', message);
  
      // üëáÔ∏è set value of input field
      setMessage('New value');
    };
  
    return (
      <div>
        <input
          type="text"
          id="message"
          name="message"
          onChange={handleChange}
          value={message}
        />
  
        <h2>Message: {message}</h2>
  
        <button onClick={handleClick}>Click</button>
      </div>
    );
  };

export default ButtonChangeValue;
