import React, { Component } from 'react'
 
class ButtonChangeValue extends Component {
  constructor({title_left, title_right, startValue}){
    super({title_left, title_right, startValue})
     
    // Set initial state
    this.state = {msg : startValue}
     
    // Binding this keyword
    this.handleClick = this.handleClick.bind(this)
  }
 
  clickDown(){
    startValue -= 1
    if (startValue < 0)
        startValue = 0
    this.setState({msg : startValue})
  }

  clickUp(){
    startValue += 1
    this.setState({msg : startValue})
  }

  render(){
    return (
      <div>
        <h2>Message :</h2>
        <p>{this.state.msg}</p>         
        {/* Set click handler */}
        <button onClick={this.handleClick}>
          Click here!
        </button>
      </div>
    )
  }
}

export default ButtonChangeValue;
