import React, { Component } from 'react'
import stylesProduct from '../screens/CSS/productInfor.module.css';
import {useState} from 'react';

function ButtonChangeValue({titleLeft, titleRight, startValue}){
    const [count, setCount] = useState(startValue);

    return (
      <div className={stylesProduct.soldProduct}>
        <button 
            className={stylesProduct.button}
            onClick={() => setCount(count - 1 < 0 ? 0 : count - 1)}
        >
            {titleLeft}
        </button>
        <p> {count} </p>
        <button 
            className={stylesProduct.button}
            onClick={() => setCount(count + 1)}
        >
            {titleRight}
        </button>
      </div>
    );
  };

export default ButtonChangeValue;
