import Header from './header';
import Footer from './footer';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import { CardActionArea } from '@mui/material';
import styles from '../screens/CSS/home.module.css';
import styleShop from '../screens/CSS/shop.module.css';
import stylesProduct from '../screens/CSS/productInfor.module.css';
import imageTest from '../assets/testproduct.jpg'
import ProductCategory from '../components/productCategory';
import ProductPopular from '../components/productPopular';
import RecommendProduct from '../components/recommendProduct';
import React, { useEffect, useState, useRef, memo } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import stylesProductManger from '../screens/CSS/productManager.module.css'
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import { Link } from 'react-router-dom';
import clsx from 'clsx';
import Axios from 'axios';
import axios from '../config/axios';

import Snackbar from '@mui/material/Snackbar';
import MuiAlert from '@mui/material/Alert';
import ImageUploader from '../components/imageUploader';

import stylesForm from './CSS/RegisterFormCSS.module.scss';

const Alert = React.forwardRef(function Alert(props, ref) {
  return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});

const defautlAvatar =
  'https://res.cloudinary.com/trinhvanthoai/image/upload/v1655489389/thoaiUploads/defaultAvatar_jxx3b9.png';

function ProductManager(products) {
    const posts = [
      {id: "1", name: "Mo hinh 1", cost: 30000, solded: 3, count: 100, rating: 4.3},
      {id: "2", name: "Mo hinh 2", cost: 40000, solded: 1, count: 130, rating: 4.7},
      {id: "3", name: "Mo hinh 3", cost: 50000, solded: 2, count: 160, rating: 4.5},
      {id: "4", name: "Mo hinh 4", cost: 60000, solded: 3, count: 120, rating: 4.1}
    ];

    const [open, setOpen] = useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);

    const [name, setName] = useState('');
    const [detail, setDetail] = useState('');
    const [count, setCount] = useState(0);
    const [cost, setCost] = useState(0);
    const [type, setType] = useState('');
    const [avatarImg, setAvatarImg] = useState(defautlAvatar);

    // info
    const [errMsg, setErrMsg] = useState('');
    const [success, setSuccess] = useState(false);
      //console.log(avatarImg);

    const handleCloseAvatar = (event, reason) => {
      if (reason === 'clickaway') {
        return;
      }
      setOpen(false);
      setErrMsg('');
    };

    //submit product
    const handleSubmit = async (e) => {
      e.preventDefault();
      const REGISTER_URL = `/api/v1/registerProduct`;
  
      if (errMsg === '') {
        //console.log(avatarImg);
        let data = {};
        data = {
          name: name,
          detail: detail,
          count: count,
          cost: cost,
          type: type,
          avatar: avatarImg,
        };
        console.log(data);
        try {
          const response = await axios.post(REGISTER_URL, JSON.stringify(data), {
            headers: {
              'Content-Type': 'application/json',
            },
            // withCredentials: true,
          });
          console.log(JSON.stringify(response?.data));
          console.log(JSON.stringify(response));
          setSuccess(true);
        } catch (err) {
          if (!err?.response) {
            setErrMsg('No Server Response');
            setOpen(true);
          } else {
            setErrMsg('Thêm sản phẩm.');
            console.log(err);
            setOpen(true);
          }
        }
      }
    };

    useEffect(() => {
      if (errMsg !== '') {
        setOpen(true);
      }
    }, [errMsg]);

    return (
      <div className={styles.Home}>
        <Header />
        <Modal
          open={open}
          aria-labelledby="modal-modal-title"
          aria-describedby="modal-modal-description"
        >
          <Box  className={stylesProductManger.box}>
            <div className={stylesProductManger.wraper}>  
                <p> Nhập thông tin sản phẩm mới </p>
            </div>

            // form
            <div className={clsx(stylesForm.row, stylesForm.formRow)}>
              <div className={clsx(stylesForm.formLeft)}>
                <div className={clsx(stylesForm.formRow, stylesForm.row)}>
                  <div className={clsx(stylesForm.formField, stylesForm.col3)}>
                    <label
                      htmlFor="name"
                      className={clsx(stylesForm.formLabel, stylesForm.row)}
                    >
                      Tên sản phẩm:
                    </label>
                    <input
                      id="name"
                      name="name"
                      value={name}
                      onChange={(e) => setName(e.target.value)}
                      type="text"
                      className={clsx(stylesForm.formInput, stylesForm.row)}
                      placeholder="Tên sản phẩm..."
                      required
                    />
                  </div>

                  <div className={clsx(stylesForm.formField, stylesForm.col3)}>
                    <label
                      htmlFor="detail"
                      className={clsx(stylesForm.formLabel, stylesForm.row)}
                    >
                      Mô tả:
                    </label>
                    <input
                      id="detail"
                      name="detail"
                      type="detail"
                      value={detail}
                      onChange={(e) => setDetail(e.target.value)}
                      className={clsx(stylesForm.formInput, stylesForm.row)}
                      placeholder="Mô tả..."
                      required
                    />
                  </div>

                </div>

                <div className={clsx(stylesForm.formRow, stylesForm.row)}>
                  <div className={clsx(stylesForm.formField, stylesForm.col3)}>
                    <label
                      htmlFor="count"
                      className={clsx(stylesForm.formLabel, stylesForm.row)}
                    >
                      Số lượng:
                    </label>
                    <input
                      id="count"
                      name="count"
                      type="number"
                      value={count}
                      onChange={(e) => setCount(e.target.value)}
                      className={clsx(stylesForm.formInput, stylesForm.row)}
                      required
                    />
                  </div>

                  <div className={clsx(stylesForm.formField, stylesForm.col3)}>
                    <label
                      htmlFor="cost"
                      className={clsx(stylesForm.formLabel, stylesForm.row)}
                    >
                      Số lượng:
                    </label>
                    <input
                      id="cost"
                      name="cost"
                      type="number"
                      value={cost}
                      onChange={(e) => setCost(e.target.value)}
                      className={clsx(stylesForm.formInput, stylesForm.row)}
                      required
                    />
                  </div>

                  <div className={clsx(stylesForm.formField, stylesForm.col3)}>
                    <label
                      htmlFor="type"
                      className={clsx(stylesForm.formLabel, stylesForm.row)}
                    >
                      Số lượng:
                    </label>
                    <input
                      id="type"
                      name="type"
                      type="text"
                      value={type}
                      onChange={(e) => setType(e.target.value)}
                      className={clsx(stylesForm.formInput, stylesForm.row)}
                      required
                    />
                  </div>

                </div>

                {role === 'shopper' && (
                  <div className={clsx(styles.formRow, styles.row)}>
                    <div className={clsx(styles.formField, styles.col3)}>
                      <label
                        htmlFor="cmnd/cccd"
                        className={clsx(styles.formLabel, styles.row)}
                      >
                        Số CMND/CCCD:
                      </label>
                      <input
                        id="cmnd/cccd"
                        name="cccd"
                        type="text"
                        value={cmnd}
                        onChange={(e) => setCmnd(e.target.value)}
                        className={clsx(styles.formInput, styles.row)}
                        placeholder="Số CMND/CCCD..."
                        required
                      />
                    </div>

                    <div className={clsx(styles.formField, styles.col3)}>
                      <label
                        htmlFor="noiCap"
                        className={clsx(styles.formLabel, styles.row)}
                      >
                        Nơi cấp:
                      </label>
                      <input
                        id="noiCap"
                        name="noiCap"
                        type="text"
                        value={noiCap}
                        onChange={(e) => setNoiCap(e.target.value)}
                        className={clsx(styles.formInput, styles.row)}
                        placeholder="Nơi cấp..."
                        required
                      />
                    </div>

                    <div className={clsx(styles.formField, styles.col3)}>
                      <label
                        htmlFor="ngayCap"
                        className={clsx(styles.formLabel, styles.row)}
                      >
                        Ngày cấp:
                      </label>
                      <input
                        id="ngayCap"
                        name="ngayCap"
                        type="date"
                        value={ngayCap}
                        onChange={(e) => setNgayCap(e.target.value)}
                        className={clsx(styles.formInput, styles.row)}
                        required
                      />
                    </div>
                  </div>
                )}

                <div className={clsx(styles.formRow, styles.row)}>
                  <div className={clsx(styles.formField, styles.col3)}>
                    <label
                      htmlFor="address"
                      className={clsx(styles.formLabel, styles.row)}
                    >
                      Địa chỉ:
                    </label>
                    <input
                      id="address"
                      name="address"
                      type="text"
                      value={address}
                      onChange={(e) => setAddress(e.target.value)}
                      className={clsx(styles.formInput, styles.row)}
                      placeholder="Địa chỉ..."
                      required
                    />
                  </div>

                  <div className={clsx(styles.formField, styles.col3)}>
                    <label
                      htmlFor="password"
                      className={clsx(styles.formLabel, styles.row)}
                    >
                      Mật khẩu:
                    </label>
                    <input
                      id="password"
                      name="password"
                      type="password"
                      onChange={(e) => setPassword(e.target.value)}
                      className={clsx(styles.formInput, styles.row)}
                      placeholder="Mật khẩu..."
                      required
                    />
                  </div>

                  <div className={clsx(styles.formField, styles.col3)}>
                    <label
                      htmlFor="confirmPassword"
                      className={clsx(styles.formLabel, styles.row)}
                    >
                      Nhập lại mật khẩu:
                    </label>
                    <input
                      id="confirmPassword"
                      name="confirmPassword"
                      type="password"
                      onChange={(e) => setConfirmPassword(e.target.value)}
                      className={clsx(styles.formInput, styles.row)}
                      placeholder="Nhập lại mật khẩu..."
                      required
                    />
                  </div>
                </div>
              </div>

              <div className={clsx(styles.avatarInput, styles.col)}>
                <ImageUploader
                  avatarImg={avatarImg}
                  onAvatarChange={setAvatarImg}
                />
              </div>
            </div>


            <Button onClick={handleClose}>Close</Button>
          </Box>
        </Modal>


        <div className={styles.content} >
          <div className={styles.wraper}>
            <div className={stylesProductManger.tdisplay}>  
                <p>Quản lý sản phẩm</p>
                <Button href="#text-buttons" onClick={handleOpen}>Thêm sản phẩm</Button>
            </div>
            
            <TableContainer component={Paper}>
              <Table sx={{ minWidth: 650 }} aria-label="simple table">
                <TableHead>
                  <TableRow>
                    <TableCell> ID </TableCell>
                    <TableCell align="right"> Sản phẩm </TableCell>
                    <TableCell align="right"> Giá tiền </TableCell>
                    <TableCell align="right"> Đã bán </TableCell>
                    <TableCell align="right"> Kho </TableCell>
                    <TableCell align="right"> Đánh giá </TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {posts.map((product) => (
                    <TableRow
                      key={product.id}
                      sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                    >
                      <TableCell component="th" scope="row">
                        {product.id}
                      </TableCell>
                      <TableCell align="right">{product.name}</TableCell>
                      <TableCell align="right">{product.cost}</TableCell>
                      <TableCell align="right">{product.solded}</TableCell>
                      <TableCell align="right">{product.count}</TableCell>
                      <TableCell align="right">{product.rating}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>

          </div>
        </div>
        <Footer />
      </div>
    );
  }
  
  export default ProductManager;
