import React, { Component } from 'react'
 
class ButtonChangeValue extends Component {
  constructor({title_left, title_right, startValue}){
    super({title_left, title_right, startValue})
     
    // Set initial state
    this.state = {msg : startValue}
    this.title_left = {msg : title_left}
    this.title_right = {msg : title_right}
    // Binding this keyword
    this.clickDown = this.clickDown.bind(this)
    this.clickUp = this.clickUp.bind(this)
  }
 
  clickDown(){
    console.log(this.state.msg)
    this.state.msg = int(this.state.msg) - 1
    if (this.state.msg < 0)
        this.state.msg = 0
    console.log(this.state.msg)
  }

  clickUp(){
    this.state.msg += 1
    console.log("tang")
  }

  render(){
    return (
      <div>
        <button onClick={this.clickDown}>
          {this.title_left.msg}
        </button>
        <p>{this.state.msg}</p>         
        <button onClick={this.clickUp}>
          {this.title_right.msg}
        </button>
      </div>
    )
  }
}

export default ButtonChangeValue;
